import react from "react";
import FetchAllReports from "./FetchAllReports";
import React, { useState } from "react";
import { QueryClient, QueryClientProvider, usePaginationQuery } from "react-query";

const queryClient = new QueryClient();

const fetchAll = async (page) => {
 // const res = await fetch('https://jsonplaceholder.typicode.com/photos/?page=${page}');
  const res = await fetch('https://swapi.dev/api/planets/page=${page}');
  return res.json();
};

const AllList = () => {
  const [page, setPage] = useState(1);
  const { data, status } = usePaginationQuery(['all', page],() => fetchAll(page));
  //console.log("data ", data, "status", status);
 // const {data, status} = useQuery(['all', page], ()=>fetchAll(page));
  return (
    <div>
      <h2>AllList</h2>
      <button onClick={()=> setPage(1)} >page1</button>
      <button onClick={()=> setPage(2)} >page2</button>
      <button onClick={()=> setPage(3)} >page3</button>


      {status === "loading" && <div>Loading. Please Wait</div>}
      {status === "error" && <div>Error fetching data</div>}
      {status === "success" && (
        <div>
          {data.map((item) => (<FetchAllReports key={item.id} item={item} />
          ))}
        </div>
      )}
    </div>
  );
};

export default function Wrapped() {
  return (
    <QueryClientProvider client={queryClient}>
      <AllList />
    </QueryClientProvider>
  );
}
