  const pagesVisited = pageNum * cardsPerPage;  
  const AllReports = profile
    .slice(pagesVisited, pagesVisited + cardsPerPage)
    .map( (profile) => {
      return (
      <div>{profile.id}</div>


      
      ) 
    })
    
    
    


    return <div>{AllReports}</div>

  }


