import {useState} from 'react';
import Backdrop from "./Backdrop";
import Modal from "./Modal"

//props are object args
//{ } signals to exe as js, not default plain txt or html
// useState is array of 2

function Perps(props) { 
    const [modalOpen, setModalOpen] = useState(false);    //modal is open = false
    
    function deleteHandler() {
        setModalOpen(true)
    }

  return (<div className="card">
    <h2>{props.name}</h2>
    <div className="actions">
      <button className="btn" onClick={deleteHandler}>Delete</button>
    </div>

    {modalOpen ? <Modal/> :null } //conditional rendering if modalOpen is true ?, then Modal :else null

    <Backdrop/>



  </div>)
}

export default Perps
