import ReactPaginate from "react-paginate";
import mockData from "../mockData.js";
import {FaPen} from 'react-icons/fa';
import React, { useState} from "react";


function Pagination(){
  const [profile, setProfile]= useState(mockData.slice(0,100));
  const [pageNum, setPageNum] = useState(0)
  const cardsPerPage = 8;             
  const pagesVisited = pageNum * cardsPerPage;  
  const AllReports = profile.slice(pagesVisited, pagesVisited + cardsPerPage)
 
  
    

    const pageCount = Math.ceil(profile.length/cardsPerPage)
    const changePage =({selected}) => {
      setPageNum(selected);
    }

      return (<div>{AllReports}
        <ReactPaginate
          previousLabel={"prev"}
          nextLabel={"next"}
          pageCount={pageCount}
          onPageChange={changePage}
        //pagination styles 
          containerClassName={"paginationBtns"}
          previousLinkClassName={""}
          nextLinkClassName={""}
          disabledClassName={""}
          activeClassName={""} /> </div>)
}

export default Pagination;
